import Foundation

let input = """

"""

func firstMarkerIndex(with size: Int) -> Int? {
  guard let index = input.windows(ofCount: size).compactMap({ Set($0) }).firstIndex(where: { $0.count == size }) else { return nil }
  return index + size
}



struct PartOne {
  func main() {
    guard let result = firstMarkerIndex(with: 4)?.description else { return }
    print(result)
  }
}

struct PartTwo {
  func main() {
    guard let result = firstMarkerIndex(with: 14)?.description else { return }
    print(result)
  }
}


PartOne().main()
PartTwo().main()
